let%expect_test "" =
  let str = 
    {|
      type 'a list = 
        | Nil
        | Cons of 'a * 'a list
      ;;

      external leq : 'a. 'a -> 'a -> bool = "%less_than_equal";;

      let rec insert = 
        fun x t ->
          match t with
          ( Nil -> Cons (x, Nil)
          | Cons (y, t) ->
            if leq x y 
              then Cons (x, Cons (y, t))
              else Cons (y, insert x t) 
          )
      ;;

      let rec sort =
        fun t -> 
          match t with
          ( Nil -> Nil
          | Cons (x, t) -> insert x (sort t)
          )
      ;;

      let _ = sort (Cons ('p', Cons ('i', Cons ('m', Cons ('c', Cons ('s', Cons ('h', Nil)))))));;
    |}
