      let rec add = 
        fun t k d ->
          match t with
          ( Nil -> Cons ((k, d), Nil)
          | Cons ((k', d'), t) ->
              if eq k k'
                then Cons ((k, d), t)
                else Cons ((k', d'), add t k d)
          ) 
      ;;

      let rec remove = 
        fun t k ->
          match t with
          ( Nil -> Nil
          | Cons ((k', d'), t) ->
              if eq k k'
                then t
                else Cons ((k', d'), remove t k)  
          )
      ;;

      let mem = 
        fun t k ->
          try (let _ = find t k in true) with
          ( Not_found -> false )
      ;;
    |}
